var timeRemaining = global.get("timeRemaining") || 0;

// Less than 10 minutes remaining
if(timeRemaining < 10 * 1000 * 60){
    msg.chapterNum = 4;
}
// Less than 20 minutes remaining
else if (timeRemaining < 20 * 1000 * 60) {
    msg.chapterNum = 3;
}
// Less than 30 minutes remaining
else if (timeRemaining < 30 * 1000 * 60) {
    msg.chapterNum = 2;
}
// Less than 40 minutes remaining
else if (timeRemaining < 40 * 1000 * 60) {
    msg.chapterNum = 1;
}
// More than 40 minutes remaining
else {
    msg.chapterNum = 0;
}

return msg;

// Retrieve current time
var now = new Date();
// Extract the hour and minute components
var h = now.getHours();
var m = now.getMinutes();
// Add the time elements to the message object
msg.h = h;
msg.m = m;
// And pass along the flow
return msg;

// Retrieve the ID of the prop used in this flow
var propid = flow.get("payload.id");

// Get the current stored value of this prop,
// Or create a new entry if we didn't previously know about it
var prop = global.get(propid) || {};

// Now loop over the incoming values and update the stored state
var count = 0;
for (var key in msg.payload) {
  if (msg.payload.hasOwnProperty(key)) {
    prop[key] = msg.payload[key];
    count++;
  }
}

// Save the global value again
global.set(propid, prop);

// Pass the updated status through the message flow
msg.payload = prop;

// Update the dashboard editor
node.status({fill:"green", shape:"dot", text:count + " values updated"});

// Pass the value along the flow
return msg;

